import{_ as e,c as o,o as a,ae as i}from"./chunks/framework.70bNUV4I.js";const h=JSON.parse('{"title":"Shading & Retro Effects","description":"","frontmatter":{},"headers":[],"relativePath":"samples/Editor/WorldSettings/shading&retroeffects.md","filePath":"samples/Editor/WorldSettings/shading&retroeffects.md"}'),s={name:"samples/Editor/WorldSettings/shading&retroeffects.md"};function n(l,t,r,d,g,f){return a(),o("div",null,[...t[0]||(t[0]=[i('<h1 id="shading-retro-effects" tabindex="-1">Shading &amp; Retro Effects <a class="header-anchor" href="#shading-retro-effects" aria-label="Permalink to &quot;Shading &amp; Retro Effects&quot;">​</a></h1><p>This section covers the specialized rendering features designed to replicate the distinct visual limitations and &quot;imperfections&quot; of 5th and 6th-generation game consoles. By moving away from modern pixel-perfect accuracy, these effects allow you to dial in a specific retro-aesthetic—ranging from the high-fidelity color gradients of the Dreamcast to the iconic &quot;jittery&quot; geometry of the PlayStation 1.</p><br><table tabindex="0"><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">What it does (Simple Terms)</th><th style="text-align:left;">Why use it?</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Gouraud Shading</strong></td><td style="text-align:left;">Enables the classic 32-bit era vertex-lighting model.</td><td style="text-align:left;">Use this to achieve the iconic &quot;sharp&quot; color gradients and jitter-friendly lighting seen in PS1 and Dreamcast titles.</td></tr><tr><td style="text-align:left;"><strong>Vertex Jitter (Passive)</strong></td><td style="text-align:left;">Makes the corners of objects shake slightly.</td><td style="text-align:left;">Gives that classic &quot;unstable&quot; 3D look where things vibrate a little</td></tr><tr><td style="text-align:left;"><strong>Vertex Snapping (Active)</strong></td><td style="text-align:left;">Forces objects to &quot;snap&quot; to a pixel grid.</td><td style="text-align:left;">This creates the heavy &quot;wobble&quot; effect seen in PS1 games when objects move</td></tr><tr><td style="text-align:left;"><strong>Affine Texture Warping</strong></td><td style="text-align:left;">Disables &quot;perspective correction&quot; for textures.</td><td style="text-align:left;">Makes textures look like they are &quot;swimming&quot; or warping when you look at them from an angle</td></tr><tr><td style="text-align:left;"><strong>Screen Color Quantization</strong></td><td style="text-align:left;">Limits the number of colors on the whole screen.</td><td style="text-align:left;">Perfect for making your game look like it&#39;s running on an old 16-bit or 32-bit computer</td></tr><tr><td style="text-align:left;"><strong>Texture Color Quantization</strong></td><td style="text-align:left;">Limits the colors on individual objects only.</td><td style="text-align:left;">Makes textures look &quot;crunchy&quot; and low-quality without affecting the UI or background</td></tr><tr><td style="text-align:left;"><strong>Screen Dithering</strong></td><td style="text-align:left;">Adds a pattern of tiny dots to blend colors.</td><td style="text-align:left;">Helps hide &quot;color banding&quot; (ugly lines in gradients) and adds a vintage grainy texture</td></tr><tr><td style="text-align:left;"><strong>CRT Artifacts</strong></td><td style="text-align:left;">Simulates an old-fashioned tube TV screen.</td><td style="text-align:left;">Adds horizontal scanlines and a slight &quot;glow&quot; to make the game feel like it&#39;s on a 1990s TV</td></tr></tbody></table>',4)])])}const u=e(s,[["render",n]]);export{h as __pageData,u as default};
